import { StatusBar } from 'expo-status-bar';
import { TextInput,Alert, Button,StyleSheet, Text, View } from "react-native";
import React, { Component, useState } from "react";
import { LinearGradient } from 'expo-linear-gradient';
import estilos from '../estilos';
import AsyncStorage from '@react-native-async-storage/async-storage';



export default function Abastecimento() {

    const [nomeVeiculo, setNomeVeiculo] = useState('');
    const [consumoGasolina, setConsumoGasolina] = useState('');
    const [consumoAlcool, setConsumoAlcool] = useState('');
    const [precoGasolina, setPrecoGasolina] = useState('');
    const [precoAlcool, setPrecoAlcool] = useState('');
    const [inputNomeVeiculo, setNomeVeiculo1] = useState('');
    const [inputConsumoGasolina, setInputConsumoGasolina] = useState('');
    const [inputConsumoAlcool, setInputConsumoAlcool] = useState('');
    const [inputPrecoGasolina, setInputPrecoGasolina] = useState('');
    const [inputPrecoAlcool, setInputPrecoSAlcool] = useState('');
    const [date, setDate] = useState(new Date().toDateString());

    
    function verificaEntrada() {
        if (inputNomeVeiculo === '') {
            Alert.alert("Informe o nome do veiculo");
            return;
        }
        else if (inputConsumoGasolina === '') {
            Alert.alert("Informe o consumo em gasolina do veiculo");
            return;
        }
        else if (inputConsumoAlcool === '') {
            Alert.alert("Informe o consumo em alcool do veiculo");
            return;
        }
        else{
            GuardaDadosStorage();
            Alert.alert("Dados Salvos!");
            setInputNomeVeiculo('');
            setInputConsumoGasolina('');
            setInputConsumoAlcool('');
            setInputPrecoGasolina('');
            setInputPrecoSAlcool('');
            return;
        }
    }

    async function GuardaDadosStorage()
    {
        setNomeVeiculo(inputNomeVeiculo);
        setConsumoGasolina(inputConsumoGasolina);
        setConsumoAlcool(inputConsumoAlcool);
        setPrecoGasolina(inputPrecoGasolina);
        setPrecoAlcool(inputConsumoAlcool);
        setDate(date);
        await NativeAsyncLocalStorage.setItem('nomeVeiculo','consumoGasolina','cosumoAlcool','precoGasolina','precoAlcool','dataAtual',
        inputNomeVeiculo,inputConsumoGasolina,inputConsumoAlcool,inputPrecoGasolina,inputPrecoAlcool,date);
    }


   
    return (

            <View style={estilos.container}>
                
                <TextInput style={estilos.input}
                    placeholder="Digite o Nome Veiculo"
                    value ={inputNomeVeiculo}
                    

                />
                <TextInput style={estilos.input}
                    placeholder="Digite o Consumo na Gasolina"
                    keyboardType="decimal-pad"
                    value = {inputConsumoGasolina}
                    onChangeText={(consumoG) => setInputConsumoGasolina(consumoG)}

                />
                <TextInput style={estilos.input}
                    placeholder="Digite o Consumo no Alcool"
                    keyboardType="decimal-pad"
                    value = {inputConsumoAlcool}
                    onChangeText={(consumoA) => setInputConsumoAlcool(consumoA)}

                />
                <TextInput style={estilos.input}
                    placeholder="Digite o Preço da Gasolina"
                    keyboardType="decimal-pad"
                    value = {inputPrecoGasolina}
                    onChangeText={(precoG) => setInputPrecoGasolina(precoG)}

                />
                <TextInput style={estilos.input}

                    placeholder="Digite o  Preço do alcool"
                    keyboardType="decimal-pad"
                    value = {inputPrecoAlcool}
                    onChangeText={(precoA) => setInputPrecoSAlcool(precoA)}

                />
                <br></br>
                <Button style={estilos.botao} onPress={verificaEntrada} title="Salvar"></Button>
            </View>
        
    );
}
