import { StatusBar } from 'expo-status-bar';
import { TextInput, Alert, Button, StyleSheet, Text, View, FlatList, ScrollView } from "react-native";
import React, { Component, useState, useEffect } from "react";
import { LinearGradient } from 'expo-linear-gradient';
import estilos from '../estilos';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useNavigation } from '@react-navigation/native';

export default function ListaAbastecimento() {


    const [ListaAbastecimento, setLista] = useState([
        {
            nomeVeiculo: '',
            consumoGasolina: '',
            consumoAlcool: '',
            precoGasolina: '',
            precoAlcool: '',
            date: ''
        }
    ]);

    useEffect(() => {
        RecuperaDadosStorage();
    }, []);
        

    async function RecuperaDadosStorage() {
        try {
            const lista = await AsyncStorage.getItem('ListaAbastecimento');
            if (lista !== null)
                setLista(JSON.parse(lista));
        }
        catch (error) {
            Alert.alert("Lista de Abastecimento nao foi carregada!");
        }
    }

   const renderItem = ({ListaAbastecimento, index}) => 
    <View>
      <View>
       <Text>
         {'Nome do Veiculo:' + ListaAbastecimento.nomeVeiculo+'\n'}
         {'Consumo na Gasolina:' + ListaAbastecimento.consumoGasolina+'\n'}
         {'Consumo no Alcool:' + ListaAbastecimento.consumoAlcool+'\n'}
       </Text>
     </View>
    </View>

    return (
        //<ScrollView style={estilos.background}>

        <View>
            <FlatList
                contentContainerStyle={{backgroundColor :'#444'}} 
                data={ListaAbastecimento}
                renderItem={renderItem}
                keyExtractor={(item.nomeVeiculo)}
                
            />
            <Text>
                Nome do Veiculo: {item.nomeVeiculo}
                </Text>
        </View>
            
        //</ScrollView>
    );

}
