import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import { TextInput } from "react-native";
import React, { Component } from "react";
import { LinearGradient } from 'expo-linear-gradient';
import { Alert, Button } from 'react-native-web';
import estilos from './estilos'




class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nomeVeiculo: '',
      consumoGasolina: '',
      consumoAlcool: '',
      precoGasolina: '',
      precoAlcool: ''
    };

    this.setNomeVeiculo = this.setNomeVeiculo.bind(this);
    this.setConsumoGasolina = this.setConsumoGasolina.bind(this);
    this.setConsumoAlcool = this.setConsumoAlcool.bind(this);
    this.setPrecoGasolina = this.setPrecoGasolina.bind(this);
    this.setPrecoAlcool = this.setPrecoAlcool.bind(this);
  }

  setNomeVeiculo(texto) {
    this.setState({ nomeVeiculo: texto })
  }
  setConsumoGasolina(num) {
    this.setState({ consumoGasolina: num })
  }
  setConsumoAlcool(numalcool) {
    this.setState({ consumoAlcool: numalcool })
  }
  setPrecoGasolina(numprecoGasolina) {
    this.setState({ precoGasolina: numprecoGasolina })
  }
  setPrecoAlcool(numprecoAlcool) {
    this.setState({ precoAlcool: numprecoAlcool })
  }


  verificaEntrada() {
    if (this.nomeVeiculo.trim() == "") {
      return Alert.alert("Informe o nome do veiculo");
    }
    if (this.consumoGasolina.trim() == "") {
      return Alert.alert("Informe o consumo em gasolina do veiculo");
    }
    if (this.consumoAlcool.trim() == "") {
      return Alert.alert("Informe o consumo em alcool do veiculo");
    }
  }

  render() {
    return (
      <View style={estilos.fundo}>
        <View style={estilos.container}>
          <Text style={{ color: "white", fontSize: 20 }}> AppCombustiveis</Text>
          <br></br>
          <LinearGradient
            colors={['rgba(38,125,20,1)', 'transparent']}
            style={estilos.background}
          />

          <TextInput style={estilos.input}
            placeholder="Digite o Nome Veiculo"
            onChangeText={this.setNomeVeiculo}

          />
          <TextInput style={estilos.input}
            placeholder="Digite o Consumo na Gasolina"
            keyboardType="decimal-pad"
            onChangeText={this.setConsumoGasolina}

          />
          <TextInput style={estilos.input}
            placeholder="Digite o Consumo no Alcool"
            keyboardType="decimal-pad"
            onChangeText={this.setConsumoAlcool}

          />
          <TextInput style={estilos.input}
            placeholder="Digite o Preço da Gasolina"
            keyboardType="decimal-pad"
            onChangeText={this.setPrecoGasolina}

          />
          <TextInput style={estilos.input}

            placeholder="Digite o  Preço do alcool"
            keyboardType="decimal-pad"
            onChangeText={this.setPrecoAlcool}

          />
          <br></br>
          <Button style={estilos.botao} onPress={this.verificaEntrada} title="Salvar"></Button>
        </View>
      </View>
    );

  }
}

export default App;
