import { StatusBar } from 'expo-status-bar';
import { TextInput, Alert, Button, StyleSheet, Text, View, FlatList, ScrollView } from "react-native";
import React, { Component, useState, useEffect } from "react";
import { LinearGradient } from 'expo-linear-gradient';
import estilos from '../estilos';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useNavigation } from '@react-navigation/native';
import { SafeAreaView } from 'react-navigation';


export default function Acompanhamento({ListaAbastecimento})
{
    
    const [lista, setLista] = useState([
        {
            id: 0,
            nomeVeiculo: '',
            consumoGasolina: '',
            consumoAlcool: '',
            precoGasolina: '',
            precoAlcool: '',
            date: ''
        }
    ]);

    return(
        <View>
                <Text>{'nome Veiculo:'+lista.id}</Text>
        </View>
    )


        useEffect(() => {
        RecuperaDadosStorage();
    }, []);


    async function RecuperaDadosStorage() {
        try {
            const lista = await AsyncStorage.getItem('ListaAbastecimento');
            if (lista !== null)
                setLista(JSON.parse(lista)); 
        }
        catch (error) {
            Alert.alert("Lista de Abastecimento nao foi carregada!");
        }
    }
}
