import { StatusBar } from 'expo-status-bar';
import { TextInput, Alert, Button, StyleSheet, Text, View, FlatList, ScrollView } from "react-native";
import React, { Component, useState, useEffect } from "react";
import { LinearGradient } from 'expo-linear-gradient';
import estilos from '../estilos';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useNavigation } from '@react-navigation/native';

export default function ListaAbastecimento() {


    const [ListaAbastecimento, setLista] = useState([
        {
            nomeVeiculo: '',
            consumoGasolina: '',
            consumoAlcool: '',
            precoGasolina: '',
            precoAlcool: '',
            date: ''
        }
    ]);

    useEffect(() => {
        RecuperaDadosStorage();
    }, []);

    const Item = ({ ListaAbastecimento }) => (
        <View>
          <Text>{ListaAbastecimento.nomeVeiculo}</Text>
        </View>
      );

    const renderItem = ({ ListaAbastecimento }) => (
        <Item nomeVeiculo={ListaAbastecimento.nomeVeiculo} />
    );

        

    async function RecuperaDadosStorage() {
        try {
            const lista = await AsyncStorage.getItem('ListaAbastecimento');
            if (lista !== null)
                setLista(JSON.parse(lista));
        }
        catch (error) {
            Alert.alert("Lista de Abastecimento nao foi carregada!");
        }
    }

    return (
        //<ScrollView style={estilos.background}>

        <View>
            <FlatList
                data={ListaAbastecimento}
                renderItem={renderItem}
                keyExtractor={(item) => item.nomeVeiculo}

            />
        </View>
            
        //</ScrollView>
    );

}
