import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import { TextInput } from "react-native";
import React from "react";
import { LinearGradient } from 'expo-linear-gradient';
import { Alert, Button } from 'react-native-web';



export default function App() {
  const [nomeVeiculo, setNomeVeiculo] = React.useState();
  const [consumoGasolina, setConsumoGasolina] = React.useState();
  const [consumoAlcool, setConsumoAlcool] = React.useState();
  const [precoGasolina, setPrecoGasolina] = React.useState();
  const [precoAlcool, setPrecoAlcool] = React.useState();

  return (
    <View style={styles.container}>
      <LinearGradient
        colors={['rgba(38,125,20,1)', 'transparent']}
        style={styles.background}
        />

      <TextInput style={styles.input}
        placeholder="Nome Veiculo"
        keyboardType="default"
        onChangeText={setNomeVeiculo}
        value={nomeVeiculo}
      />
      <TextInput style={styles.input}
        placeholder="Comsumo na Gasolina"
        keyboardType="decimal-pad"
        onChangeText={setConsumoGasolina}
        value={consumoGasolina}
      />
      <TextInput style={styles.input}
        placeholder="Comsumo no Alcool"
        keyboardType="decimal-pad"
        onChangeText={setConsumoAlcool}
        value={consumoAlcool}
      />
      <TextInput style={styles.input}
        placeholder="Preço da Gasolina"
        keyboardType="decimal-pad"
        onChangeText={setPrecoGasolina}
        value={precoGasolina}
      />
      <TextInput style={styles.input}

        placeholder="Preço do alcool"
        keyboardType="decimal-pad"
        onChangeText={setPrecoAlcool}
        value={precoAlcool}
      />
 <Button onClick={verificaEntrada()} ></Button>
    </View>
  );


  function verificaEntrada(){
    if(nomeVeiculo.trim() == ""){
      return Alert.alert("Informe o nome do veiculo");
    }
    if(consumoGasolina.trim() == ""){
      return Alert.alert("Informe o consumo em gasolina do veiculo");
    }
    if(consumoAlcool.trim() == ""){
      return Alert.alert("Informe o consumo em alcool do veiculo");
    }
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'blue',
    alignItems: 'center',
    justifyContent: 'center'
  },
  input: {
    borderColor: 'blue',
    backgroundColor: 'white',
    width: "50%",
    borderRadius: 10,
    borderWidth: 1,
    height: 50, textAlign: 'center'
  },
  background: {
    position: 'absolute',
    left: 0,
    right: 0,
    top: 0,
    height: 300,
  }
});
